using FS.TimeTracking.Shared.Attributes;

namespace FS.TimeTracking.Shared.Models.Shared
{
    /// <summary>
    /// Enum to describe relation between validated properties of <see cref="CompareToAttribute"/>
    /// </summary>
    /// <autogeneratedoc />
    public enum ComparisonType
    {
        /// <summary>
        /// Properties are equal
        /// </summary>
        Equal,

        /// <summary>
        /// Properties not equal
        /// </summary>
        NotEqual,

        /// <summary>
        /// Validated property is less than other property
        /// </summary>
        LessThan,

        /// <summary>
        /// Validated property is less than or equal other property
        /// </summary>
        LessThanOrEqual,

        /// <summary>
        /// Validated property is greater than other property
        /// </summary>
        GreaterThan,

        /// <summary>
        /// Validated property is greater or equal than other property
        /// </summary>
        GreaterThanOrEqual,
    }
}
