using System;
using System.Collections.Generic;
using System.ComponentModel.DataAnnotations;
using System.ComponentModel.DataAnnotations.Schema;

namespace FS.TimeTracking.Tool.Models.Imports;

/// <summary>
/// Activity
/// </summary>
public class KimaiV1Activity
{
    /// <summary>
    /// Gets the unique identifier for <see cref="Activity.Id"/>.
    /// </summary>
    /// <autogeneratedoc />
    [NotMapped]
    public Guid Id { get; } = Guid.NewGuid();

    /// <summary>
    /// Gets the unique identifier.
    /// </summary>
    [Required]
    public int ActivityId { get; set; }

    /// <summary>
    /// Gets or sets the short/display name.
    /// </summary>
    [Required]
    [StringLength(100)]
    public string Name { get; set; }

    /// <summary>
    /// Gets or sets a comment.
    /// </summary>
    public string Comment { get; set; }

    /// <summary>
    /// Gets or sets a value indicating whether this item is hidden.
    /// </summary>
    [Required]
    public bool Trash { get; set; }

    /// <summary>
    /// Gets or sets a value indicating whether this item is hidden.
    /// </summary>
    [Required]
    public bool Visible { get; set; }

    /// <summary>
    /// Gets or sets the projects related to this activity.
    /// </summary>
    public List<KimaiV1ProjectActivity> Projects { get; set; } = new();
}